docker-slim build --tag codeclimate/codeclimate-yamllint:slim --http-probe=false --exec 'yamllint . && yamllint --version' --mount "$PWD/tests/example:/work" --workdir '/work' --include-path '/usr/lib/python3.9/site-packages/yamllint' --include-path '/usr/lib/python3.9/site-packages/yaml' --include-path '/usr/lib/python3.9/site-packages/pathspec' codeclimate/codeclimate-yamllint:latest && prettier --write slim.report.json